A = (0,-0.5,0.5*1.5);
B = (0,0,0);
C = (0,0.8,0.8*1.5);

draw(surface((-1,-1,0)--(-1,1,0)--(1,1,0)--(1,-1,0)--cycle),paleyellow,nolight);
draw((-1,-1,0)--(-1,1,0)--(1,1,0)--(1,-1,0)--cycle);
